package com.example.kotlin2rmk

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.example.kotlin2rmk.databinding.ActivityMainBinding
import com.example.kotlin2rmk.extensions.glide
import com.example.kotlin2rmk.extensions.showToast
import kotlin.random.Random

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding
    private val images = arrayListOf(
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQI3itw2c9Rw626koIYg5zB6RJJTl1ip3pDwQ&usqp=CAU",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQI-SaYWlXmVicHWYEEpRgrmFir507tWQk3pA&usqp=CAU",
        "https://images.unsplash.com/photo-1598124146163-36819847286d?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8anBnfGVufDB8fDB8fA%3D%3D&auto=format&fit=crop&w=800&q=60",
        "https://images.unsplash.com/photo-1601902572612-3c850fab3ad8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8NHx8anBnfGVufDB8fDB8fA%3D%3D&auto=format&fit=crop&w=800&q=60",
        "https://images.unsplash.com/photo-1585468491047-f02d6f7c706c?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8M3x8anBnfGVufDB8fDB8fA%3D%3D&auto=format&fit=crop&w=800&q=60",
        "https://images.unsplash.com/photo-1639241423587-a9802ac301bc?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTB8fGpwZ3xlbnwwfHwwfHw%3D&auto=format&fit=crop&w=800&q=60",
        "https://images.unsplash.com/photo-1640681858035-5e62f72d90a8?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTZ8fGpwZ3xlbnwwfHwwfHw%3D&auto=format&fit=crop&w=800&q=60",
        "https://images.unsplash.com/photo-1646418579944-397ac052b78a?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTl8fGpwZ3xlbnwwfHwwfHw%3D&auto=format&fit=crop&w=800&q=60",
        "https://images.unsplash.com/photo-1565028832942-d005b1bd84f0?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTd8fGpwZ3xlbnwwfHwwfHw%3D&auto=format&fit=crop&w=800&q=60"
    )
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)
        showCartons()
    }

    private fun showCartons() {
        binding.apply {
            btnRandom.setOnClickListener {
                val randomImage = Random.nextInt(images.size)
                val randomIndex = images[randomImage]
                imgCarton.glide(randomIndex)
            }
            btnSubmit.setOnClickListener {
                if (etLink.text.toString().isEmpty()){
                    showToast(getString(R.string.toast_url))
                } else {
                    images.add(etLink.text.toString())
                    showToast(getString(R.string.toast_wrong_url))
                }
            }
        }

    }
}